mapper.update:
UPDATE	Users
SET		nickname	= #{nickname},
			telecom	= #{telecom},
			phone		= #{phone}
WHERE		user_id	= #{user_id}

UPDATE	Users
SET		password 	= #{password}
WHERE		user_id 		= #{user_id}

mapper.select:
SELECT rImg.r_img, p.biz_id, rImg.r_id, p.user_id FROM Accom a, Room r, Room_img rImg, Reservation resv, Payment p, Users u WHERE a.biz_id = r.biz_id AND r.biz_id = rImg.biz_id AND r.biz_id = resv.biz_id AND resv.biz_id = p.biz_id AND rImg.biz_id = resv.biz_id AND rImg.biz_id = p.biz_id
AND u.user_id = resv.user_id AND resv.user_id = p.user_id
AND r.r_id = rImg.r_id AND r.r_id = resv.r_id AND resv.r_id = p.r_id AND rImg.r_img_id = '1'
AND p.r_id IN
#{r_id}
AND p.user_id = #{login_id}

mapper.delete:
DELETE FROM Soldout
WHERE		biz_id	= #{biz_id}
AND		resv_id	= #{resv_id}
AND		r_id		= #{r_id}

DELETE FROM Payment
WHERE		pay_id	= #{pay_id}
AND		biz_id	= #{biz_id}
AND		resv_id	= #{resv_id}
AND		r_id		= #{r_id}

DELETE FROM Reservation
WHERE		biz_id	= #{biz_id}
AND		resv_id	= #{resv_id}
AND		r_id		= #{r_id}

mapper.select:
SELECT rv.pay_id, rvImg.review_img_id, rvImg.review_img FROM Review rv, Review_img rvImg WHERE rv.pay_id = rvImg.pay_id
AND rvImg.pay_id IN
#{payId}

SELECT  review_img.*
FROM    Review_Img
WHERE   pay_id = #{pay_id}

mapper.update:
UPDATE  Review
SET     rating 		  = #{rating},
        review_date 	  = sysdate,
        review_content = #{review_content, jdbcType=VARCHAR}
WHERE   pay_id = #{pay_id}

