mapper.select:
select max(r_id)
from room
where biz_id = #{biz_id}

select max(r_img_id)
from room_img
where biz_id = #{biz_id}
	and r_id = #{r_id}

select r_img
from room_img
where biz_id = #{biz_id}
    and r_id = #{r_id}

select r.*, a.biz_name
from room r
inner join accom a on a.biz_id = r.biz_id
where r.biz_id = #{biz_id}
    and r_id = #{r_id}

mapper.delete:
delete room_img
where biz_id = #{biz_id}
	and r_id = #{r_id}

mapper.update:
update room
set r_avail = '210'
where biz_id = #{biz_id}
	and r_id = #{r_id}

update room
set r_avail = '220'
where biz_id = #{biz_id}
	and r_id = #{r_id}

update room
set r_avail = '230'
where biz_id = #{biz_id}
	and r_id = #{r_id}

mapper.select:
select r_img
from room_img
where biz_id = #{biz_id}
