mapper.select:
SELECT	Count(*)
FROM		Users
WHERE		nickname = #{nickname}

mapper.update:
UPDATE	Users
SET		nickname	= #{nickname},
			telecom	= #{telecom},
			phone		= #{phone}
WHERE		user_id	= #{user_id}

UPDATE	Users
SET		password 	= #{password}
WHERE		user_id 		= #{user_id}

mapper.delete:
DELETE FROM Soldout
WHERE		biz_id	= #{biz_id}
AND		resv_id	= #{resv_id}
AND		r_id		= #{r_id}

DELETE FROM Payment
WHERE		pay_id	= #{pay_id}
AND		biz_id	= #{biz_id}
AND		resv_id	= #{resv_id}
AND		r_id		= #{r_id}

DELETE FROM Reservation
WHERE		biz_id	= #{biz_id}
AND		resv_id	= #{resv_id}
AND		r_id		= #{r_id}

mapper.select:
SELECT      a.biz_id, a.biz_name, a.addr
FROM        Accom a, Room r
WHERE       a.biz_id = r.biz_id	
AND       	r.biz_id = #{biz_id}
AND         r.r_id   = #{r_id}

SELECT rv.pay_id, rvImg.review_img_id, rvImg.review_img FROM Review rv, Review_img rvImg WHERE rv.pay_id = rvImg.pay_id
AND rvImg.pay_id IN
#{payId}

SELECT  review_img
FROM    Review_Img
WHERE   pay_id=#{pay_id}

mapper.update:
UPDATE	Users
SET		user_status = 'deactivated'
WHERE		user_id 		= #{user_id}

mapper.insert:
INSERT INTO Out
VALUES	(#{user_id}
		   , #{out_price,	jdbcType=VARCHAR}	
		   , #{out_uncom,	jdbcType=VARCHAR}
		   , #{out_nouse,	jdbcType=VARCHAR}
		   , #{out_secure,	jdbcType=VARCHAR}
		   , #{out_content,	jdbcType=VARCHAR}
)
