mapper.select:
select count(*)
from accom
where biz_id = #{biz_id}

mapper.update:

update accom
set room_count = (
                  select count(*)
                  from room
                  where biz_id = #{biz_id}
                  )
where biz_id = #{biz_id}


update accom
set room_count = (
                  select count(*) +1
                  from room
                  where biz_id = #{biz_id}
                  )
where biz_id = #{biz_id}


update accom
set room_count = (
                  select count(*) -1
                  from room
                  where biz_id = #{biz_id}
                  )
where biz_id = #{biz_id}

update accom
set accom_avail = '210'
where biz_id = #{biz_id}

update accom
set accom_avail = '220'
where biz_id = #{biz_id}

update accom
set accom_avail = '230'
where biz_id = #{biz_id}
